"use strict";

import "core-js/modules/es.string.pad-start.js";
import "core-js/modules/es.array.concat.js";
import "core-js/modules/es.object.to-string.js";
export default {
  name: "ImageBrowser",
  methods: {
    apiKey: function apiKey() {
      return "5NqF6jh9w3uVhB9rwEeUJfSIwBgcavPvzYEO7lH3";
    },
    managePage: function managePage() {
      var _this = this;

      this.setup();
      var today = document.getElementById("search-box").value;
      this.fetchAPI(today);
      document.getElementById("search-button").addEventListener("click", function (event) {
        event.preventDefault();
        var selectedDate = document.getElementById("search-box").value;

        _this.fetchAPI(selectedDate);
      });
    },
    setup: function setup() {
      var now = new Date();
      var dd = String(now.getDate()).padStart(2, "0");
      var mm = String(now.getMonth() + 1).padStart(2, "0"); //January is 0!

      var yyyy = now.getFullYear();
      var today = "".concat(yyyy, "-").concat(mm, "-").concat(dd);
      var maxDateAttr = document.createAttribute("max");
      var valueAttr = document.createAttribute("value");
      maxDateAttr.value = today;
      valueAttr.value = today;
      document.getElementById("search-box").setAttributeNode(maxDateAttr);
      document.getElementById("search-box").setAttributeNode(valueAttr);
    },
    fetchAPI: function fetchAPI(selectedDate) {
      document.getElementById("result").innerHTML = "";
      var resultHeader = document.createElement("h3");
      resultHeader.id = "result-header";
      document.getElementById("result").appendChild(resultHeader);
      var mediaTitle = document.createElement("h4");
      mediaTitle.id = "media-title";
      document.getElementById("result").appendChild(mediaTitle);
      var resultContainer = document.createElement("div");
      resultContainer.id = "result-container";
      document.getElementById("result").appendChild(resultContainer);
      var URL = "https://api.nasa.gov/planetary/apod?api_key=".concat(this.apiKey(), "&date=").concat(selectedDate);
      fetch(URL).then(function (response) {
        console.log(response);
        return response.json();
      }).then(function (json) {
        console.log(json);
        resultHeader.innerText = "Astronomy ".concat(json.media_type, " for ").concat(selectedDate);
        var mediaContainer = document.createElement("div");
        mediaContainer.id = "media-container";
        mediaTitle.innerText = json.title;
        var mediaDiv = document.createElement("div");
        mediaContainer.appendChild(mediaDiv);

        if (json.media_type === "image") {
          mediaDiv.id = "image";
          var image = document.createElement("img");
          var imgLink = document.createAttribute("src");
          imgLink.value = json.url;
          image.setAttributeNode(imgLink);
          mediaDiv.appendChild(image);
        } else if (json.media_type === "video") {
          mediaDiv.id = "video";
          var video = document.createElement("iframe");
          var videoLink = document.createAttribute("src");
          videoLink.value = json.url;
          video.setAttributeNode(videoLink);
          mediaDiv.appendChild(video);
        }

        document.getElementById("result-container").appendChild(mediaContainer);
        var explanationContainer = document.createElement("div");
        explanationContainer.id = "explanation-container";
        var explanation = document.createElement("p");
        explanation.textContent = json.explanation;
        explanationContainer.appendChild(explanation);
        document.getElementById("result-container").appendChild(explanationContainer);
      });
    }
  }
};
